-- Seed 100 mock profiles and subscriptions
-- Idempotent: uses ON CONFLICT DO NOTHING

-- Helper function to generate slugs from shop names
CREATE OR REPLACE FUNCTION generate_slug(shop_name TEXT) RETURNS TEXT AS $$
BEGIN
  RETURN lower(regexp_replace(shop_name, '[^a-zA-Z0-9]+', '-', 'g'));
END;
$$ LANGUAGE plpgsql;

-- Paid users (1-50) with active subscriptions
ON CONFLICT (id) DO NOTHING;

-- Free users (51-100)
ON CONFLICT (id) DO NOTHING;

-- Active subscriptions for paid users (1-50)
ON CONFLICT (id) DO NOTHING;

-- Drop the helper function
DROP FUNCTION IF EXISTS generate_slug(TEXT);